package basics_otherbook;

public class equals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String x="MINDq";
		String y="mindq";
		if(x.equalsIgnoreCase(y))
		{
			System.out.println("Same");
		}
		else
		{
			System.out.println("not same");
		}
	}

}
